executeFunc((function authVk(){const authLink=document.getElementById("vk-auth");console.log(authLink);if(typeof authLink==="undefined"||authLink===null){return false}authLink.addEventListener("click",(function(event){const modal=document.getElementById("modal");if(modal){let currentmodal=bootstrap.Modal.getOrCreateInstance(modal);currentmodal.hide()}event.preventDefault();const authUrl=this.href;const width=600;const height=700;const left=(screen.width-width)/2;const top=(screen.height-height)/2;const features=`width=${width},height=${height},left=${left},top=${top},resizable=yes,scrollbars=yes`;const authWindow=window.open(authUrl,"vkAuth",features);if(authWindow){authWindow.focus()}else{alert("Не удалось открыть окно авторизации. Разрешите всплывающие окна.")}}));window.addEventListener("message",(function(event){if(event.data.type==="VK_AUTH_SUCCESS"){window.location.replace(window.location.origin)}}));return true}));